import React, { Component } from "react";
import ReactFlexyTable from 'react-flexy-table';
import 'react-flexy-table/dist/index.css';

class AdminPanel extends Component {
    componentDidMount = async () => {
        let response = await this.props.functions.getAllEntities();
        this.setState({ response: response });
    }

    approveEntity = async (address) => {
        var response = await this.props.functions.approveEntity(address);
        response = await this.props.functions.getAllEntities();
        this.setState({response: response});
    }

    state = {
        response: null,
        clicked: false
    }

    render() {
        let table;

      var dataFromContract = this.state.response;



        if (this.state.response != null) {
            var j;
            for (j = 0; j < dataFromContract.length; j++) {
                var i=j;
                if (!dataFromContract[i].approved) {
                    dataFromContract[i] = {
                        name: dataFromContract[i].name, entityAddress: dataFromContract[i].entityAddress, approved: "X",
                        // button: <button className="buttonForm" onClick={() => this.approveEntity(this.state.response[i].entityAddress)}>Aprobar</button>
                        button: <button value={i} className="buttonTable" onClick={this.approveEntity.bind(this, dataFromContract[i].entityAddress)}>Aprobar</button>
                    }
                }
                else {
                    dataFromContract[i] = {
                        name: dataFromContract[i].name, entityAddress: dataFromContract[i].entityAddress, approved: <span>&#10003;</span>,
                        button: <span></span>
                    }
                }
            }
            console.log(dataFromContract)
            console.log(this.state.response)
            let columns = [
                {
                    header: 'Usuario',
                    key: 'name',
                    td: (data) => <div>{data.name}</div>
                },
                {
                    header: 'Direcci贸n',
                    key: 'entityAddress',
                    td: (data) => <div>{data.entityAddress}</div>
                },
                {
                    header: 'Confirmado',
                    // can also use with nested objects
                    key: 'approved',
                    td: (data) => <div>{data.approved}</div>
                },
                {
                    header: 'Acci贸n',
                    // can also use with nested objects
                    key: 'button',
                    td: (data) => <div>{data.button}</div>
                }
            ]

            table = <ReactFlexyTable data={dataFromContract} columns={columns} filterable nonFilterCols={["approved","button"]} sortable pageSizeOptions={[20, 50, 100]} pageSize={20} className="my_table rft-table" />

        }



        return (
            <div className="maincomp">
                <div className="searchTable">{table}</div>
            </div>
        )
    }
}

export default AdminPanel;
