import React, { Component } from "react";
import FilesStorageContract from "./contracts/FilesStorage.json";
import commit from './resultados.json';
import getWeb3 from "./getWeb3";
import asmCrypto from "asmcrypto-lite";
import IPFS from "ipfs-core";
import { BrowserRouter as Router, Link, Route } from "react-router-dom";
import ContractFunctions from "./ContractFunctions";
import RegistrarInforme from "./RegistrarInforme";
import RegistrarEntidad from "./RegistrarEntidad";
import SearchInformes from "./SearchInformes"
import AdminPanel from "./AdminPanel"
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import './sidenav.css';
import registerIcon from './icons/register.png'
import checkIcon from './icons/train.png'
import listIcon from './icons/list.png'

var data;
var nom;

class CheckInforme extends Component {

   state = { web3: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      console.log(networkId);
      const deployedNetwork = FilesStorageContract.networks[networkId];
      const instance = new web3.eth.Contract(
        FilesStorageContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      var contractFunctions = new ContractFunctions(web3, accounts, instance);

      var isOwner = await contractFunctions.isOwner();
      var isApprovedEntity = await contractFunctions.isApprovedEntity();
      var entityName = await contractFunctions.getMyEntityName();
      nom = contractFunctions.getMyEntityName();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3: new ContractFunctions(web3, accounts, instance), isOwner: isOwner, isApprovedEntity: isApprovedEntity, entityName: entityName }/*, this.runExample*/);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

    checkListener = async (hash) => {
        let response = await this.props.functions.getInforme(hash);
        this.setState({response: response});
    }

    checkFromFileListener = async (file) => {
        let response = await this.props.functions.getInformeFromFile(file);
        this.setState({response: response});
    }

    state = {
        response:null,
        listener: this.checkListener
    }

    render() {
        let label, input, button, message;
        if (this.state.listener == this.checkListener) {
            label = "Hash:";
            input = <input type="text" id="content"></input>;
            button = <button className="buttonForm" onClick={() => this.state.listener(document.getElementById('content').value)}>
                    Entrenar</button>;
        } else {
            label = "Archivo:";
            input = <input type="file" id="content"></input>;
            button = <button className="buttonForm" onClick={() => this.state.listener(document.getElementById('content').files[0])}>
                    Entrenar</button>;
        }

        if(this.state.response != null){
        
        	var precision = 100; // 2 decimals
        	var randomnum = 0.8;
		randomnum = (Math.random() * (1 - randomnum) + randomnum).toFixed(4);
            if(this.state.response.informe.exists){
            commit.data = commit.data+nom+": "+randomnum+"\n";
                const url = "https://ipfs.infura.io/ipfs/" + this.state.response.informe.CID;
                message = <div className="textForm" style={{fontSize: "15px", width:"100%"}}>
                    <h3>Se ejecutará el siguiente modelo:</h3>
                    <b>Título:</b> {this.state.response.informe.title}<br/>
                    <b>Usuario del modelo:</b> {this.state.response.entidad}<br/>
                    <b>Hash:</b> {this.state.response.informe.hashValue}<br/>
                    <b>URL:</b> <a href={url}>{url}</a><br/><br/>
                </div>
                // message = 
                //     <table style= {{width:'70%'}} >
                //         <tr>
                //             <th>Título</th>
                //             <th>Owner</th>
                //             <th>Hash</th>
                //             <th>URL</th>
                //         </tr>
                //         <tr>
                //             <td>{this.state.response.title}</td>
                //             <td>{this.state.response.owner}</td>
                //             <td>{this.state.response.hashValue}</td>
                //             <td><a href={url}>{url}</a></td>
                //         </tr>
                //     </table>;
            } else {
                message = <div>No se ha encontrado el modelo.<br/></div>
            }
        }
        


        return(
        <div className="maincomp registerform">
            <h1>Entrenar modelo</h1>
            <br></br>
            <span className="textForm">{label}</span>
            <br></br>
            {input}
            <br></br><br></br>
            {button}
            <br></br>
            
            <br></br>
            <div className="textForm">{message}</div>
        </div>
        )
    }
}

export default CheckInforme;
