'use strict';

const debug = require('debug');

const log = Object.assign(debug('libp2p:ip-port-to-multiaddr'), {
  error: debug('libp2p:ip-port-to-multiaddr:err')
});

const _require = require('multiaddr'),
      Multiaddr = _require.Multiaddr;

const errCode = require('err-code');

const _require2 = require('ip-address'),
      Address4 = _require2.Address4,
      Address6 = _require2.Address6;

const errors = {
  ERR_INVALID_IP_PARAMETER: 'ERR_INVALID_IP_PARAMETER',
  ERR_INVALID_PORT_PARAMETER: 'ERR_INVALID_PORT_PARAMETER',
  ERR_INVALID_IP: 'ERR_INVALID_IP'
};
/**
 * Transform an IP, Port pair into a multiaddr
 *
 * @param {string} ip
 * @param {number|string} port
 */

function ipPortToMultiaddr(ip, port) {
  if (typeof ip !== 'string') {
    throw errCode(new Error("invalid ip provided: ".concat(ip)), errors.ERR_INVALID_IP_PARAMETER);
  }

  if (typeof port === 'string') {
    port = parseInt(port);
  }

  if (isNaN(port)) {
    throw errCode(new Error("invalid port provided: ".concat(port)), errors.ERR_INVALID_PORT_PARAMETER);
  }

  try {
    // Test valid IPv4
    new Address4(ip); // eslint-disable-line no-new

    return new Multiaddr("/ip4/".concat(ip, "/tcp/").concat(port));
  } catch {}

  try {
    // Test valid IPv6
    const ip6 = new Address6(ip);
    return ip6.is4() ? new Multiaddr("/ip4/".concat(ip6.to4().correctForm(), "/tcp/").concat(port)) : new Multiaddr("/ip6/".concat(ip, "/tcp/").concat(port));
  } catch (err) {
    const errMsg = "invalid ip:port for creating a multiaddr: ".concat(ip, ":").concat(port);
    log.error(errMsg);
    throw errCode(new Error(errMsg), errors.ERR_INVALID_IP);
  }
}

module.exports = ipPortToMultiaddr;
module.exports.Errors = errors;
