import React, { Component } from "react";

class RegistrarEntidad extends Component {
    state = {response:null}

    registerListener = async (name) => {
        const response = await this.props.functions.registerEntity(name);
        this.setState({response: response});
    }

    render() {
        return(
        <div className="maincomp registerform">
            <h1>Registrarse como entidad</h1>
            <br></br>
            <span className="textForm">Nombre de la entidad:</span> <br></br>
            <input type="text" id="name"></input>
            <br></br><br></br><br></br>
            <button className="buttonForm" onClick={() => this.registerListener(
                document.getElementById('name').value)}>
                Registrarse
            </button>
            <br></br>
            <p className="textForm">{this.state.response}</p>
        </div>
        )
    }
}

export default RegistrarEntidad;
