import React, { Component } from "react";
import FilesStorageContract from "./contracts/FilesStorage.json";
import getWeb3 from "./getWeb3";
import asmCrypto from "asmcrypto-lite";
import IPFS from "ipfs-core";
import { BrowserRouter as Router, Link, Route } from "react-router-dom";
import ContractFunctions from "./ContractFunctions";
import RegistrarInforme from "./RegistrarInforme";
import RegistroUser from "./RegistroUser";
import CheckInforme from "./CheckInforme";
import SearchInformes from "./SearchInformes"
import AdminPanel from "./AdminPanel"
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import './sidenav.css';
import registerIcon from './icons/register.png'
import checkIcon from './icons/train.png'
import listIcon from './icons/list.png'

import "./App.css";

class Server extends Component {
  state = { web3: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      console.log(networkId);
      const deployedNetwork = FilesStorageContract.networks[networkId];
      const instance = new web3.eth.Contract(
        FilesStorageContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      var contractFunctions = new ContractFunctions(web3, accounts, instance);

      var isOwner = await contractFunctions.isOwner();
      var isApprovedEntity = await contractFunctions.isApprovedEntity();
      var entityName = await contractFunctions.getMyEntityName();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3: new ContractFunctions(web3, accounts, instance), isOwner: isOwner, isApprovedEntity: isApprovedEntity, entityName: entityName }/*, this.runExample*/);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  registerInforme = async (hash, title) => {
    const { accounts, contract } = this.state;

    await contract.methods.registerInforme(hash, title).send({ from: accounts[0] });
  }

  getInforme = async (hash) => {
    const { accounts, contract } = this.state;

    const response = await contract.methods.getInforme(hash).call();

    this.setState({ titulo: response });
  }

  readFile = async (file) => {
    console.log(file);
    let data = await file.arrayBuffer();
    let uint8data = new Uint8Array(data);
    let hash = asmCrypto.SHA256.hex(data);

    const ipfs = await IPFS.create()
    const { cid } = await ipfs.add(uint8data)
    console.info(cid)
  }

  registrarInforme = async (file, title) => {
    const { accounts, contract } = this.state;

    let data = await file.arrayBuffer();
    let hash = asmCrypto.SHA256.hex(data);

    await contract.methods.registerInforme(hash, title).send({ from: accounts[0] });

  }

  render() {
    var rootRoute;
    var registerRoute;
    var adminPanelLink;
    var adminPanelRoute;
    var entityName;
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    else {
      if (this.state.isApprovedEntity) {
        registerRoute = <Route path="/register" component={() => (
          <RegistrarInforme functions={this.state.web3} />
        )} />
        rootRoute = <Route exact={true} path="/" component={() => (
          <RegistrarInforme functions={this.state.web3} />
        )} />
        entityName = "Conectado como: " + this.state.entityName;
      }
      else{
        registerRoute = <Route path="/register" component={() => (
          <RegistroUser functions={this.state.web3} />
        )} />
        rootRoute = <Route exact={true} path="/" component={() => (
          <RegistroUser functions={this.state.web3} />
        )} />
      } 
      console.log(this.state.isOwner);
      if (this.state.isOwner) {
        adminPanelLink = <NavItem eventKey="adminPanel">
          <NavIcon>
            {/* <i className="fa fa-fw fa-device" style={{ fontSize: '1.75em' }} /> */}
            {/* <img src={listIcon} style={{ height: '40px', marginTop: "5px" }} /> */}
            <div>ADMIN</div>
          </NavIcon>
          <NavText>
            AdminPanel
            </NavText>
        </NavItem>
        adminPanelRoute = <Route path="/adminPanel" render={() => (
          <AdminPanel functions={this.state.web3} />
        )} />
      }
    }


    return (
      <Router>
        <div className="topnav">
          <div className="titletext"> Entrenamiento Federated Learning</div>
          <div className="loggedInAs">{entityName}</div>
        </div>
        <Route render={({ location, history }) => (
          <React.Fragment>
            <SideNav
              onSelect={(selected) => {
                const to = '/' + selected;
                if (location.pathname !== to) {
                  history.push(to);
                }
              }}
            >
              <div style={{ height: "45px" }}></div>
              <SideNav.Nav defaultSelected="register">
                <NavItem eventKey="register">
                  <NavIcon>
                    {/* <i className="fa fa-fw fa-home" style={{ fontSize: '1.75em' }} /> */}
                  <div>REGISTRO</div>  
                  </NavIcon>
                  <NavText>
                    Home
                        </NavText>
                </NavItem>
                <NavItem eventKey="train">
                  <NavIcon>
                    {/* <i className="fa fa-fw fa-device" style={{ fontSize: '1.75em' }} /> */}
                    <div>ENTRENAR</div>
                  </NavIcon>
                  <NavText>
                    Devices
                        </NavText>
                </NavItem>
                <NavItem eventKey="search">
                  <NavIcon>
                    {/* <i className="fa fa-fw fa-device" style={{ fontSize: '1.75em' }} /> */}
                    <div>LISTA</div>
                  </NavIcon>
                  <NavText>
                    List
                        </NavText>
                </NavItem>
                {adminPanelLink}
              </SideNav.Nav>
            </SideNav>
            <main>
              {rootRoute}
              {registerRoute}
              <Route path="/train" render={() => (
                <CheckInforme functions={this.state.web3} />
              )} />
              <Route path="/search" render={() => (
                <SearchInformes functions={this.state.web3} />
              )} />
              {adminPanelRoute}
            </main>
          </React.Fragment>
        )}
        />
      </Router>







      // <Router>
      //   <div className="topnav">
      //     <Link to="/register">Registrar informe</Link>
      //     <Link to="/check">Comprobar informe</Link>
      //     <Link to="/search">Buscar informes</Link>
      //   </div>

      //   <div className="App">

      //   <Route exact={true} path="/" render={() => (<h1>Welcome</h1>)} />
      //   <Route path="/register" render={() => (
      //     <RegistrarInforme functions={this.state.web3}/>
      //   )}/>
      //   <Route path="/check" render={() => (
      //     <CheckInforme functions={this.state.web3}/>
      //   )}/>
      //   <Route path="/search" render={() => (
      //     <SearchInformes functions={this.state.web3}/>
      //   )}/>


      //   </div>
      // </Router>
    );
  }
}

export default Server;
